import { useEffect, useContext } from 'react';
import * as firebase from 'firebase';
import { useNavigate } from "react-router-dom";
import { CurrentUser } from "../public/src/Components/context";


export default function checkAuthorization() {
    const navigate = useNavigate();

  useEffect(() => {
    firebase.auth().onAuthStateChanged(function(user) {
      if (!user) {
       alert('You are not signed in. Please log in to use the site.')
       const { currentUser, setCurrentUser } = React.useContext({
        name: '',
        email: '',
        balance: 0,
        password: '',
        role: 'none'
       });

       navigate('/');
      }
    });
  }, [navigate]);
}
