import React from "react";
import { Route, Link, Routes, useLocation, NavLink } from 'react-router-dom';
import { CurrentUser, Card} from "./context";
import { Navbar, Nav, Container } from 'react-bootstrap';

export default function NavBar({}){

  const { currentUser } = React.useContext(CurrentUser);

  const location = useLocation();
  const { hash, pathname, search } = location;
  let selected = `#${pathname}`
    return (
        <>

        <Navbar className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top"> 
          <Container>
            <Navbar.Brand href="#">
       <img src={require('../Images/BadBankLogo.png')} className="img-navbar" alt="Bank Logo"/>
       </Navbar.Brand>
       <Navbar.Toggle aria-controls="navbarNav" />
        <Navbar.Collapse id="navbarNav">
          <Nav className="navbar-nav mr-auto">
          {currentUser.name ? (
            <>
            <NavLink href="#/deposit/" className={selected === "#/deposit/" ? "active" : ""}>Deposit</NavLink>
            <NavLink href="#/withdraw/"className={selected === "#/withdraw/" ? "active" : ""}>Withdraw</NavLink>
            <NavLink href="#/alldata/" className={selected === "#/alldata/" ? "active" : ""}>All Data</NavLink>
            </>

          ): (
            <>
            <NavLink href="#/login/"className={selected === "#/login/" ? "active" : ""}>Log In</NavLink>
            <NavLink href="#/createaccount/" className={selected === "#/createaccount/" ? "active" : ""}>Create Account</NavLink>
            </>
          )}
          </Nav>
          {currentUser && (
               <Nav className="ml-auto">
                 <NavLink href="#/login/"className={selected === "#/login/" ? "active" : ""}>{currentUser.email}</NavLink>
               </Nav>
          )}
          </Navbar.Collapse>
        </Container>
        </Navbar>

        </>
    );
}
