  };

  const firebaseApp = initializeApp(firebaseConfig);
  const auth = getAuth();
  setPersistence(auth, inMemoryPersistence);

  async function createFirebase(name, email, password, requestedRole) {
    if (!db) {
        throw new Error('Database connection not established');
    }
    try {
        if (await checkAccount(email)) {
            return true;
        } else {
                await createUserWithEmailAndPassword(auth, email, password);
                return await create(name, email, requestedRole);          
        }
      } catch (error) {
        console.error('Error creating user with Firebase:', error.code, error.message);
        throw error;
      }
  }

  async function loginFirebase(email, password) {
    try {
        await signInWithEmailAndPassword(auth, email, password);
        const url = `/account/login/${email}`;
            var res = await fetch(url);
         var data = await res.json();
         if (data.code && data.name === 'FirebaseError') {
            alert("Something went wrong. Please check your credentials and try again.")
            return;
         }
             setCurrentUser({
                 email: data.email,
                 name: data.name,
                 balance: data.balance,
                 role: data.role
               });  
            } catch (error) {
        console.error('Error logging in with Firebase:', error.code, error.message);
        return(error);
    }
}

async function logout() {
    try {
        await signOut(auth);
        return;
    } catch (error) {
        console.error('Error during logout:', error);
        throw error;
    }
}

async function checkAuthorization() {
    const user = auth.currentUser;

    if (user) {
        const authorizationURL = `/authorization/`;
    try {
        const res = await fetch(authorizationURL);
        if (res.ok) {
            let user = await res.json();
            if (!user.email) {
                setCurrentUser({
                    name: '',
                    email: '',
                    balance: 0,
                    password: '',
                    role: 'none'
                });
                navigate('/');
            }
        }
    } catch(err) {
        console.error(err);
    }
    } else {
     return console.log(`no user found`);
    }
}

  module.exports = {createFirebase, loginFirebase, logout, checkAuthorization}
