import React from "react";
import {Table} from "./context";

export default function AllData(){
  const [data, setData] = React.useState('');
  React.useEffect(() => {
    //fetch all accounts from API
    fetch('/account/all')
    .then(response => response.json())
    .then(data => {
      console.log(data);
      setData(JSON.stringify(data));
    });
  }, []);

const userList = data.map(user => {
  return (
    <tr key={user.key}>
      <td>{user.name}</td>
      <td>${user.balance}</td>
      <td>{user.email}</td>
      <td>{user.password}</td>
      <td>{user.key}</td>
    </tr>
  );
});

return (
  <div>

  <Table
  header="User Information"
  id="usertable"
  col1="Name"
  col2="Balance"
  col3="Email"
  col4="Password"
  col5="ID"
  list={userList}

  />

  </div>

);
}
